import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';  // React Router의 Link 사용

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('http://localhost:8080/posts'); // 게시물 목록을 받아오는 API 호출
        if (!response.ok) {
          throw new Error('게시물 데이터를 가져오는 데 실패했습니다.');
        }
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (error) {
    return <div>오류 발생: {error}</div>;
  }

  return (
    <div>
      <h1>게시물 목록</h1>
      <ul>
        {posts.length > 0 ? (
          posts.map((post) => (
            <li key={post.id}>
              <Link to={`/post/${post.id}`}>
                <button>{post.title}</button> {/* 제목을 버튼으로 표시 */}
              </Link>
            </li>
          ))
        ) : (
          <p>게시물이 없습니다.</p>
        )}
      </ul>
    </div>
  );
};

export default Posts;
