import React, { useState } from 'react';
import { Routes, Route, Link, BrowserRouter } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import About from './pages/About';
import Mypage from './pages/Mypage'; // Mypage 컴포넌트 추가
import PrivateRoute from './component/PrivateRoute'; // PrivateRoute 컴포넌트 추가

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // 로그인 상태 관리

  // 로그인 성공 시 호출되는 함수
  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  // 로그아웃 시 호출되는 함수
  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  return (
    <BrowserRouter>
      <div>
        <nav>
          <Link to="/"><button>홈</button></Link>
          <Link to="/Signup"><button>회원가입</button></Link>
          <Link to="/Login"><button>로그인</button></Link>
          <Link to="/About"><button>소개</button></Link>
          {isAuthenticated && <Link to="/Mypage"><button>마이페이지</button></Link>} {/* 로그인 후 마이페이지 링크 추가 */}
          {isAuthenticated && <button onClick={handleLogout}>로그아웃</button>}
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/Login" element={<Login onLogin={handleLogin} />} />
          <Route path="/Signup" element={<Signup />} />
          <Route path="/About" element={<About />} />
          <Route
            path="/Mypage"
            element={
              <PrivateRoute isAuthenticated={isAuthenticated}>
                <Mypage />
              </PrivateRoute>
            }
          />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
