import { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [accessToken, setAccessToken] = useState(null);  // Access Token

  // 컴포넌트가 마운트될 때 localStorage에서 토큰을 읽어옵니다.
  useEffect(() => {
    const accessTokenFromStorage = localStorage.getItem('access_token');

    if (accessTokenFromStorage) {
      setAccessToken(accessTokenFromStorage);  // localStorage에서 액세스 토큰 가져오기
    }
  }, []);  // 최초 마운트 시 한 번만 실행

  // accessToken이 변경될 때마다 localStorage에 저장
  useEffect(() => {
    if (accessToken) {
      localStorage.setItem('access_token', accessToken); // Access Token 저장
    }
  }, [accessToken]);

  const login = async (id, password) => {
    const response = await fetch('http://localhost:8080/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id, password }),
    });

    if (!response.ok) {
      throw new Error('로그인 실패');
    }

    const data = await response.json();
    // 로그인 성공 시 localStorage에 액세스 토큰 저장
    setAccessToken(data.accessToken);  // 상태 업데이트
  };

  const logout = () => {
    // 상태에서 토큰을 삭제하고, localStorage에서 토큰을 삭제
    setAccessToken(null);

    // localStorage에서 토큰 삭제
    localStorage.removeItem('access_token');
  };

  return (
    <AuthContext.Provider value={{ accessToken, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to use Auth Context
export const useAuth = () => {
  return useContext(AuthContext);
};
