import React, { useState } from 'react';

function Login() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    setIsLoading(true);
    setMessage(''); // 이전 메시지 초기화
    console.log('로그인 요청 시작'); // 로그인 요청 시작 로그

    try {
      const loginData = { id, password };

      // 로그인 요청
      const response = await fetch('http://localhost:8080/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
        credentials: 'include', // 쿠키를 자동으로 포함
      });

      // 서버 응답 상태 확인
      if (!response.ok) {
        // 오류 발생 시 서버로부터 받은 에러 메시지를 가져옴
        const errorData = await response.json();
        throw new Error(errorData.error || '서버 문제');
      }

      const data = await response.json(); // 서버 응답을 JSON으로 파싱
      console.log('서버 응답 데이터:', data);

      if (data.message === '로그인 성공!') {
        setMessage('로그인 성공!');
        localStorage.setItem('token', data.token); // JWT 토큰을 로컬 스토리지에 저장
      } else {
        setMessage(data.error || '아이디 또는 패스워드가 잘못되었습니다.');
      }
    } catch (error) {
      console.error('로그인 오류 발생:', error);
      setMessage(error.message); // 오류 메시지 상태에 저장
    } finally {
      setIsLoading(false); // 로딩 상태 해제
      console.log('로그인 요청 종료');
    }
  };

  return (
    <main>
      <h2>로그인</h2>
      <input
        type="text"
        placeholder="아이디"
        value={id}
        onChange={(e) => setId(e.target.value)} // 아이디 입력
      />
      <input
        type="password"
        placeholder="패스워드"
        value={password}
        onChange={(e) => setPassword(e.target.value)} // 패스워드 입력
      />
      <button onClick={handleLogin} disabled={isLoading}>로그인</button>
      {isLoading && <p>로그인 중...</p>} {/* 로딩 중 텍스트 */}
      <p>{message}</p> {/* 로그인 성공/실패 메시지 출력 */}
    </main>
  );
}

export default Login;
