import React, { useEffect, useState } from 'react';
import { useAuth } from '../component/auth'; // AuthContext에서 가져오기

const MyPage = () => {
  const { accessToken } = useAuth();  // AuthContext에서 accessToken을 가져옴
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 컴포넌트가 마운트되면 사용자의 정보를 가져옵니다.
  useEffect(() => {
    if (!accessToken) {
      setError('토큰이 없습니다. 로그인 후 다시 시도해주세요.');
      setLoading(false);
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await fetch('http://localhost:8080/mypage', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${accessToken}`,  // Authorization 헤더에 액세스 토큰 추가
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || '사용자 정보를 가져오는 데 실패했습니다.');
        }

        const data = await response.json();
        setUserInfo(data);
      } catch (error) {
        setError(error.message);  // 오류 메시지를 상태에 저장
      } finally {
        setLoading(false);  // 로딩 종료
      }
    };

    fetchUserInfo();
  }, [accessToken]);  // accessToken이 변경될 때마다 요청을 다시 보냄

  // 로딩 중, 에러 처리 및 사용자 정보 표시
  if (loading) {
    return <div>사용자 정보를 가져오는 중...</div>;
  }

  if (error) {
    return <div>오류 발생: {error}</div>;
  }

  return (
    <div>
      <h1>사용자 정보</h1>
      {userInfo ? (
        <div>
          <p>이름: {userInfo.name}</p>
          <p>이메일: {userInfo.email}</p>
          {/* 다른 사용자 정보도 여기에 표시 */}
        </div>
      ) : (
        <p>사용자 정보를 찾을 수 없습니다.</p>
      )}
    </div>
  );
};

export default MyPage;
