import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';  // URL 파라미터를 읽기 위해 useParams 사용

const PostDetail = () => {
  const { id } = useParams();  // URL에서 id를 추출
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPostDetail = async () => {
      try {
        const response = await fetch(`http://localhost:8080/post/${id}`);  // 게시물 ID로 상세 정보 가져오기
        if (!response.ok) {
          throw new Error('게시물 상세 데이터를 가져오는 데 실패했습니다.');
        }
        const data = await response.json();
        setPost(data);  // 데이터 상태에 저장
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPostDetail();
  }, [id]);  // id가 변경될 때마다 다시 실행

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (error) {
    return <div>오류 발생: {error}</div>;
  }

  return (
    <div>
      <h1>{post.title}</h1>
      <p>{post.content}</p>  {/* 게시물 내용 표시 */}
    </div>
  );
};

export default PostDetail;
