// src/pages/Home.js
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth'; // 전역 상태를 관리하는 AuthContext

function Home() {
  const { token, logout } = useAuth(); // 전역 상태에서 인증된 사용자 확인
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate('/'); // 인증되지 않으면 로그인 페이지로 리디렉션
    }
  }, [token, navigate]);

  const handleLogout = () => {
    logout(); // 로그아웃 후 전역 상태에서 토큰 제거
  };

  return (
    <div>
      <h1>홈페이지</h1>
      <p>로그인 성공! 여기는 보호된 페이지입니다.</p>
      <button onClick={handleLogout}>로그아웃</button>
    </div>
  );
}

export default Home;
