import React, { useEffect, useState } from 'react';

const MyPage = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 컴포넌트가 마운트되면 사용자의 정보를 가져옵니다.
  useEffect(() => {
    // 로컬 스토리지에서 토큰을 가져옵니다.
    const token = localStorage.getItem('token');  // 'token'은 저장된 토큰의 키 이름입니다.

    const fetchUserInfo = async () => {
      try {
        const response = await fetch('/mypage', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: token ? `Bearer ${token}` : '',  // 토큰이 있으면 Authorization 헤더에 추가
          },
        });

        if (!response.ok) {
          throw new Error('사용자 정보를 가져오는 데 실패했습니다.');
        }

        const data = await response.json();
        setUserInfo(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, []);  // 빈 배열을 넣어서 컴포넌트가 마운트될 때만 실행되게 함

  // 로딩 중, 에러 처리 및 사용자 정보 표시
  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (error) {
    return <div>오류 발생: {error}</div>;
  }

  return (
    <div>
      <h1>사용자 정보</h1>
      {userInfo ? (
        <div>
          <p>이름: {userInfo.name}</p>
          <p>이메일: {userInfo.email}</p>
          {/* 다른 사용자 정보도 여기에 표시 */}
        </div>
      ) : (
        <p>사용자 정보를 찾을 수 없습니다.</p>
      )}
    </div>
  );
};

export default MyPage;
