import React, { useState, useEffect } from 'react';
import { useAuth } from '../component/auth'; // AuthContext에서 가져오기

const MyPage = () => {
  const { accessToken } = useAuth();  // AuthContext에서 accessToken을 가져옴
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [profileImage, setProfileImage] = useState(null); // 프로필 이미지 상태

  useEffect(() => {
    if (!accessToken) {
      setError('토큰이 없습니다. 로그인 후 다시 시도해주세요.');
      setLoading(false);
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await fetch('http://localhost:8080/mypage', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${accessToken}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || '사용자 정보를 가져오는 데 실패했습니다.');
        }

        const data = await response.json();
        setUserInfo(data);
        setProfileImage(data.profileImage); // 서버에서 받은 프로필 이미지 URL로 상태 업데이트
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, [accessToken]);

  // 프로필 사진 업로드 핸들러
  const handleProfileImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('profileImage', e.target.files[0]); // 선택된 파일을 formData에 추가

    try {
      const response = await fetch('http://localhost:8080/uploadProfileImage', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error('프로필 사진 업로드 실패');
      }

      const data = await response.json();
      setProfileImage(data.imageUrl); // 업로드된 이미지 URL로 상태 업데이트
      alert('프로필 사진이 성공적으로 업로드되었습니다!');
    } catch (error) {
      alert(error.message);
    }
  };

  if (loading) {
    return <div>사용자 정보를 가져오는 중...</div>;
  }

  if (error) {
    return <div>오류 발생: {error}</div>;
  }

  return (
    <div>
      <h1>사용자 정보</h1>
      {userInfo ? (
        <div>
          <p>이름: {userInfo.name}</p>
          <p>이메일: {userInfo.email}</p>
          <div>
            <label>프로필 사진</label>
            <input type="file" onChange={handleProfileImageUpload} />
            {profileImage && <img src={`http://localhost:8080${profileImage}`} alt="Profile" width="100" />}
          </div>
        </div>
      ) : (
        <p>사용자 정보를 찾을 수 없습니다.</p>
      )}
    </div>
  );
};

export default MyPage;
