// src/pages/Posts.js
import React, { useState, useEffect } from 'react'; 
import { useNavigate } from 'react-router-dom'; // 페이지 전환을 위해 useNavigate 추가
import { useAuth } from '../component/auth'; // useAuth 훅을 통해 accessToken을 가져옵니다.

const Posts = () => {
  const { accessToken } = useAuth(); // useAuth 훅을 사용해 accessToken을 가져옵니다.
  const [posts, setPosts] = useState([]); // 게시물 상태 관리
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [error, setError] = useState(null); // 에러 상태
  const navigate = useNavigate(); // useNavigate 훅을 사용해 페이지 전환

  // 게시물 데이터를 가져오는 함수
  const getPosts = async () => {
    if (!accessToken) {
      setError('로그인된 사용자가 아닙니다.');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/posts', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${accessToken}`, // 헤더에 accessToken 추가
        },
      });

      if (!response.ok) {
        throw new Error('게시물을 가져오는 데 실패했습니다.');
      }

      const data = await response.json();
      setPosts(data); // 가져온 게시물 데이터 상태에 저장
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // 페이지가 로드되면 게시물 데이터를 가져옵니다.
  useEffect(() => {
    getPosts();
  }, [accessToken]); // accessToken이 변경될 때마다 다시 실행

  // 게시물 클릭 시 상세 페이지로 이동
  const handlePostClick = (postId) => {
    navigate(`/posts/${postId}`); // /posts/:id 경로로 이동
  };

  if (loading) {
    return <div>게시물을 가져오는 중...</div>;
  }

  if (error) {
    return <div>오류 발생: {error}</div>;
  }

  return (
    <div>
      <h1>게시물 목록</h1>
      <div>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post.posts_id}>
              <button onClick={() => handlePostClick(post.posts_id)}>
                {post.title}
              </button>
            </div>
          ))
        ) : (
          <p>게시물이 없습니다.</p>
        )}
      </div>
    </div>
  );
};

export default Posts;
