import React, { useState } from 'react';

function Login() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:8080/login', {
        method: 'POST', // POST 메서드 사용
        headers: {
          'Content-Type': 'application/json', // 요청 헤더는 JSON 형식
        },
        body: JSON.stringify({ id, password }), // 서버에 보낼 로그인 정보
      });

      if (!response.ok) {
        throw new Error('서버문제');
      }

      const data = await response.json(); // 서버 응답을 JSON으로 파싱

      if (data.success) {
        setMessage('로그인 성공!');
      } else {
        setMessage('아이디 또는 패스워드가 잘못되었습니다.');
      }
    } catch (error) {
      setMessage(error.message); // 오류 메시지를 상태에 저장
    }
  };

  return (
    <main>
      <h2>로그인</h2>
      <input
        type="text"
        placeholder="아이디"
        value={id}
        onChange={(e) => setId(e.target.value)} // 아이디 입력
      />
      <input
        type="password"
        placeholder="패스워드"
        value={password}
        onChange={(e) => setPassword(e.target.value)} // 패스워드 입력
      />
      <button onClick={handleLogin}>로그인</button>
      <p>{message}</p> {/* 로그인 성공/실패 메시지 출력 */}
    </main>
  );
}

export default Login;
